import { useRef, useState } from "react";
import { useNavigate } from "react-router-dom";
import styles from "../input.module.css"
import axios from 'axios';

export default function Winput()
{   

    const nav = useNavigate();
    const id = localStorage.getItem('userId');
    const weightRef = useRef(null);

    function w_input(e){
        e.preventDefault();
        axios.post('http://localhost:4000/api/w_input',
        {
            id:id,
            weight:weightRef.current.value
        }
    ).catch((err)=>{
        console.log(err);
    })
    .then(()=>{
        localStorage.setItem('weight',weightRef.current.value);
        alert("체중이 변경 되었습니다.");
        nav(`/id/${id}`);
    }
    );

    axios.post('http://localhost:4000/api/w_input',
    {
        id:id,
        weight:weightRef.current.value
    }
    ).catch((err)=>{
        console.log(err);
    });
    }

    return (
        <div className={styles.hw_input}>   
            <form onSubmit={w_input}>
                <p><input type="text" placeholder="체중(kg) 변화가 기록됨." ref={weightRef}/></p>
                <p><button>적용</button></p>
            </form>
        </div>
    );
}
