import {useParams,Link } from "react-router-dom";
import styles from "../main.module.css"
import {useEffect, useState } from 'react';
import axios from "axios";
import { toast, ToastContainer } from 'react-toastify';

export default function Main(){
    const userId = localStorage.getItem('userId');
    const [myInfo,setMyInfo] = useState([]);
    const [preWeight,setPreWeight] = useState([]);
    const [today,setToday] = useState(new Date().
    toLocaleDateString('ko-KR',{year:'numeric',month:'2-digit',day:'2-digit'})
    .replace(/\./g,'-').split(' ').join("").slice(0,-1));

    useEffect(()=>{
        const timeId = setInterval(()=>{
            setToday(new Date().toLocaleDateString('ko-KR',{year:'numeric',month:'2-digit',day:'2-digit'}).replace(/\./g,'-').split(' ').join("").slice(0,-1));
        },1000);
        return (()=> clearInterval(timeId));
    },[]);

    useEffect(()=>{
        axios.post('http://localhost:4000/api/myInfo',{
            id:userId
        })
        .then((info)=>{
            setMyInfo(info.data);
        });
    },[]);

    useEffect(()=>{
        const daysAgo = 7;
        const postDate = new Date(new Date(today).getTime() - daysAgo * 24 * 60 * 60 * 1000);
        axios.post('http://localhost:4000/api/week_weight',{
            weight_id:userId,
            date:postDate.toLocaleDateString
            ('ko-KR',{year:'numeric',month:'2-digit',day:'2-digit'})
            .replace(/\./g,'-').split(' ').join("").slice(0,-1)
        })
        .then((weight)=>{
            setPreWeight(weight.data);
        })
    },[])

    useEffect(()=>{
        console.log(preWeight.weight-myInfo.weight);
        if(preWeight.weight<myInfo.weight)
        {
            toast.warn('주간 체중변화가 3키로 이상입니다! 관리하셔야 합니다.',{
                position:"top-center", // 알람 위치 지정
                autoClose:3000, // 자동 off 시간
                hideProgressBar:false, // 진행시간바 숨김
                closeOnClick:true, // 클릭으로 알람 닫기
                rtl:false, // 알림 좌우 반전
                pauseOnFocusLoss:true, // 화면을 벗어나면 알람 정지
                draggable:true, // 드래그 가능
                pauseOnHover:true, // 마우스를 올리면 알람 정지
                theme:"light",
                limit:1, // 알람 개수 제한
                toastStyle: { 
                    width: "100px", // 원하는 너비로 조정 
                    height: "50px", // 원하는 높이로 조정 
                    fontSize: "16px" // 원하는 폰트 크기로 조정 
                  } 
            });
        }
    },[myInfo,preWeight])

    return (
        <div id={styles.main}>

            <p className={styles.welcome}>{userId}님 환영합니다</p>
            <div>
            <ToastContainer/>
                <p className={styles.today}>{today}</p>
                <ul id={styles.menu_list}>
                    <li><Link className={styles.menu} to={"/id/"+userId+"/myinfo"}>회원정보</Link></li>
                    <li><Link className={styles.menu} to={"/id/"+userId+"/w_input"}>체중 변경</Link></li>
                    <li><Link className={styles.menu} to={"/id/"+userId+"/diet"}>식단기록</Link></li>
                    <li><Link className={styles.menu} to={"/id/"+userId+"/dietRecord"}>이전 식단</Link></li>
                    <li><Link className={styles.menu} to={"/id/"+userId+"/workout"}>운동관리</Link></li>
                </ul>   
            </div>
            <div className={styles.introduce}>
                <p>건강 관리를 위해 <br></br>식단과 운동을 기록할 수 있는<br></br> api를 구현했습니다.</p>
            </div>


        </div>
    );
}
