import { useRef, useState } from "react";
import { useNavigate } from "react-router-dom";
import styles from "../input.module.css"
import axios from 'axios';

export default function Input()
{   
    const firstComplete = localStorage.getItem('fc');
    const [changed,setChanged] = useState(false);
    const nav = useNavigate();
    const id = localStorage.getItem('userId');

    let height = Number(localStorage.getItem('height'));
    let weight = Number(localStorage.getItem('weight'));

    const heightRef = useRef(height);
    const weightRef = useRef(null);
    function hw_input(){
        axios.post('http://localhost:4000/api/hw_input',
        {
            id:id,
            height:heightRef.current.value,
            weight:weightRef.current.value
        }
    ).catch((err)=>{
        console.log(err);
    })
    .then(()=>{
        localStorage.setItem("fc",true);
        height=localStorage.setItem('height',heightRef.current.value);
        weight=localStorage.setItem('weight',weightRef.current.value);
        alert("신장과 체중이 등록 되었습니다.");
        nav(`/id/${id}`);
        setChanged(true);
    }

    );
    }

    function w_input(e){
        e.preventDefault();
        console.log(weightRef.current.value);
        axios.post('http://localhost:4000/api/w_input',
        {
            id:id,
            weight:weightRef.current.value
        }
    ).catch((err)=>{
        console.log(err);
    })
    .then(()=>{

        console.log(changed);
        weight=localStorage.setItem('weight',weightRef.current.value);
        alert("체중이 변경되었습니다.");
        nav(`/id/${id}`);
        setChanged(true);
    }

    );
    }

    if(height===0 || weight===0)
    {
        return (
            <div className={styles.hw_input}>
                <form onSubmit={hw_input}>
                    <p><input type="text" placeholder="신장(cm)" ref={heightRef}/></p>
                    <p><input type="text" placeholder="체중(kg)" ref={weightRef}/></p>
                    <p><button>적용</button></p>
                </form>
            </div>
        );
    }else if(height!==0&&!firstComplete){
        return (
            <div className={styles.hw_input}>
                <form onSubmit={w_input}>
                    <p><input type="text" placeholder="체중(kg)" ref={weightRef} value={weight}/></p>
                    <p><button>적용</button></p>
                </form>
            </div>
        );
    }
}
