import { useEffect, useState } from "react";
import styles from "../input.module.css"
export default function Input()
{
    const [height,setHeight] = useState(0);
    setHeight(localStorage.getItem('height'));
    useEffect(()=>{
        console.log(height);
    },[height]);

    if(height===0)
    {
        return (
            <div className={styles.hw_input}>
                <form>
                    <p><input type="text" placeholder="신장(cm)"/></p>
                    <p><input type="text" placeholder="체중(kg)"/></p>
                    <p><button>적용</button></p>
                </form>
            </div>
        );
    }else{
        return (
            <div className={styles.hw_input}>
                <form>
                    <p><input type="text" placeholder="체중(kg)"/></p>
                    <p><button>적용</button></p>
                </form>
            </div>
        );
    }

}
