import axios from "axios";
import { useRef, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import styles from "../breakfast.module.css";

export default function Breakfast()
{
    const [searchResults,setSearchResults] = useState([]);
    const nav = useNavigate();
    const {meal_time} = useParams();
    const searchRef = useRef(null);
    const foodRef = useRef(null);
    const gRef = useRef(null);
    const kclRef = useRef(null);
    const carboRef = useRef(null);
    const proteinRef = useRef(null);
    const fatRef = useRef(null);
    const userId=localStorage.getItem('userId');
    const now = new Date();
    const date =now.toISOString().split('T')[0];
    function searchFood(e)
    {
        e.preventDefault();
        axios.post('http://localhost:4000/api/food_search',{
            search:'%'+searchRef.current.value+'%'
        }).then((food)=>{
            console.log(food);
            setSearchResults(food.data);
        });
        searchRef.current.value ='';
        setSearchResults([]);
    }
    function inputBreakfast(foodData)
    {
        axios.post('http://localhost:4000/api/breakfast',{
            user_id:userId,
            meal_time:meal_time,
            food:foodData.food_name,
            food_type:foodData.food_type,
            serving_size:foodData.serving_size,
            kcl:foodData.kcal,
            carbo:foodData.carbo,
            protein:foodData.protein,   
            fat:foodData.fat,
            date 
        }).then(()=>{
            alert('아침식사가 등록되었습니다.');
            nav(-1);
        }).catch((err)=>
        {
            console.log(err);
        }
        )
    }
    return(

        <div>
            <form onSubmit={searchFood}>
                <label>음식 검색</label>
                <p><input type="search" ref={searchRef}/><button>검색</button></p>
            </form>
            {
                searchResults.length>0 &&(
                    <ul>
                        {searchResults.map((food)=>{
                            return(
                                <li className={styles.food_list} key={food.food_name}>
                                    식품명:{food.food_name},식품종류:{food.food_type},칼로리:{food.kcal}kcal,단백질:{food.protein}g,{""},
                                    지방:{food.fat}g,탄수화물:{food.carbo}g,제공량:{food.serving_size}g <button onClick={()=>inputBreakfast(food)}>등록</button>
                                </li>
                            )
                        })} 
                    </ul>
                )}
        </div>
    );
}
