import {useContext,useRef,useEffect,useState} from "react";
import {useParams,useNavigate} from "react-router-dom";
import {loginContext,idContext} from "../context/idContext";
import styles from "../login.module.css"

export default function Login(){
    const [userData,setUserData] = useState({id:'',password:'',name:'',weight:0,height:0});
    const idRef = useRef(null);
    const passwordRef = useRef(null);
    const nav = useNavigate();
    const {id,setId} = useContext(idContext);
    const {login,setLogin} = useContext(loginContext);
    function doLogin(e){
        e.preventDefault(); 
        fetch('http://localhost:4000/api/login',{
            method:"POST",
            headers:{
            "Content-Type":"application/json"   
        },
        body:JSON.stringify({
            id:idRef.current.value,
            password:passwordRef.current.value
        })
        }).then(res=> res.json())
        .then(user=>{  
            setId(`${user.id}`);
            setLogin(true);
            setUserData(user);
            localStorage.setItem('userId',user.id);
            alert(`${user.id}님 로그인`);
            if(user.height===0)
            {
                nav(`/id/${user.id}/hw_input`)
            }
            nav(`/id/${user.id}`);   
        })
    }

    return(
        <div className={styles.login}>
            <form onSubmit={doLogin}>
                <p><input type="text" placeholder="id" ref={idRef}/></p>
                <p><input type="text" placeholder="password" ref={passwordRef}/></p>
                <p><input type="submit" value="로그인"/></p>
            </form>
        </div>
    )
}
