import {useParams,Link } from "react-router-dom";
import styles from "../main.module.css"
import {useEffect } from 'react';

export default function Main(){
    const userId = localStorage.getItem('userId');
    let today = new Date();
    let year = today.getFullYear(); // 년도
    let month = today.getMonth() + 1;  // 월
    let date = today.getDate();  // 날짜
    let day = today.getDay();
    let hours = today.getHours(); // 시
    let minutes = today.getMinutes();  // 분    
    return (
        <div id={styles.main}>
            <p>{userId}님 환영합니다</p>
            <div>
                <p>{year}년 {month}월 {date}일 <br/>{hours}시 {minutes}분</p>

                <ul id={styles.menu_list}>
                    <li><Link className={styles.menu} to={"/id/"+userId+"/hw_input"}>신장및 체중 변경</Link></li>
                    <li><Link className={styles.menu} to={"/id/"+userId+"/diet"}>식단기록</Link></li>
                </ul>


            </div>
            <div className="introduce">
                <p>건강 관리를 위해 <br></br>식단과 운동을 기록할 수 있는<br></br> api를 구현했습니다.</p>
            </div>

        </div>
    )
}
