import {useRef,useEffect,useState } from "react";
import {useNavigate} from "react-router-dom";
import styles from "../register.module.css"

export default function Register()
{
    const idRef = useRef(null);
    const passwordRef = useRef(null);
    const passwordAgainRef = useRef(null);
    const nameRef = useRef(null);
    const genderRef = useRef(null);
    const nav = useNavigate();

    function onRegister(e){
        e.preventDefault();
        if(passwordRef.current.value!==passwordAgainRef.current.value)
        {
            alert("비밀번호와 재확인 비밀번호가 다릅니다.");
            return 
        }
        console.log(idRef.current.value);
        console.log(passwordRef.current.value);
        console.log(nameRef.current.value);
        fetch('http://localhost:4000/api/register',{
        method:"POST",
        headers:{
            "Content-Type":"application/json"   
        },
        body:JSON.stringify({
            id:idRef.current.value,
            password:passwordRef.current.value,
            name:nameRef.current.value,
            height:0,
            weight:0,
            gender:genderRef.current.value    
        })
        }).then(res=>{
        if(res.ok){
            alert("회원가입 완료");
            nav('/');
        }
        })
        }


    return(
    <div className={styles.register}>
        <form onSubmit={onRegister}>
            <p>
                <input type="text" placeholder="id"ref={idRef}/>
            </p>
            <p>
                <input type="text" placeholder="password" ref={passwordRef}/>
            </p>
            <p>
                <input type="text" placeholder="again password" ref={passwordAgainRef}/>
            </p>
            <p>
                <input type="text" placeholder="name" ref={nameRef}/>
            </p>
            <p>
                <label>성별</label>
                <select>
                    <option value="male" ref={genderRef}>남자</option>
                    <option value="female" ref={genderRef}>여자</option>
                </select>
            </p>
            <p>
                <button type="submit">가입</button>
            </p>
        </form>

    </div>
    )
}
