import axios from "axios";
import { useEffect, useRef, useState } from "react";
import styles from "../workout.module.css";
export default function WorkOut()
{
    const userId = localStorage.getItem('userId');
    const resistanceRef = useRef(null);
    const cardioRef = useRef(null)
    const [exercises,setExercises] = useState([]);
    const [selectedExercise,setSelectedExercise] = useState(null);

    return(
        <div>
            <div id={styles.workout_regist}>
                <p>운동 기록하기</p>
                <form>
                    <p>
                        <select onChange={(e)=>setSelectedExercise(e.target.value)}>
                            <option value="cardio" ref={cardioRef}>유산소 운동</option>
                            <option value="resistance" ref={resistanceRef}>무산소 운동</option>
                        </select>
                        <button onClick={(e)=>{
                            e.preventDefault();
                            console.log(cardioRef);
                            console.log(cardioRef);
                            if(selectedExercise==="cardio"&&cardioRef.current!==null)
                            {
                                setExercises([...exercises,{type:"cardio"}]);
                            }
                            else if(selectedExercise!==null&&resistanceRef.current==="resistance"){
                                setExercises([...exercises,{type:"resistance"}]);

                            }
                            setSelectedExercise(null); 
                        }}>추가</button>
                    </p>
                    {exercises.map((exercise,index)=>(
                        <div key={index}>
                            {exercise.type==="cardio" ? (<p>유산소 등록창</p>):(<p>무산소 등록창</p>)}
                        </div>
                    ))}
                </form>
            </div>
        </div>
    );
}
