import {useParams,Link } from "react-router-dom";
import styles from "../main.module.css"
import {useEffect, useState } from 'react';
import axios from "axios";
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
export default function Main(){
    const userId = localStorage.getItem('userId');
    const [showToast,setShowToast] = useState(false);
    const [myInfo,setMyInfo] = useState([]);
    const [preWeight,setPreWeight] = useState([]);
    const [today,setToday] = useState(new Date().
    toLocaleDateString('ko-KR',{year:'numeric',month:'2-digit',day:'2-digit'})
    .replace(/\./g,'-').split(' ').join("").slice(0,-1));

    useEffect(()=>{
        const timeId = setInterval(()=>{
            setToday(new Date().toLocaleDateString('ko-KR',{year:'numeric',month:'2-digit',day:'2-digit'})
            .replace(/\./g,'-').split(' ').join("").slice(0,-1));
        },1000);
        return (()=> clearInterval(timeId));
    },[]);

    useEffect(()=>{
        axios.post('http://localhost:4000/api/myInfo',{
            id:userId
        })
        .then((info)=>{
            setMyInfo(info.data);
        });
    },[]);

    useEffect(()=>{
        const daysAgo = 7;
        const postDate = new Date(new Date(today).getTime() - daysAgo * 24 * 60 * 60 * 1000);
        axios.post('http://localhost:4000/api/week_weight',{
            weight_id:userId,
            date:postDate.toLocaleDateString
            ('ko-KR',{year:'numeric',month:'2-digit',day:'2-digit'})
            .replace(/\./g,'-').split(' ').join("").slice(0,-1)
        })
        .then((weight)=>{
            setPreWeight(weight.data);
        })
    },[])

    useEffect(()=>{
        if(showToast && (myInfo.weight-preWeight.weight)>=3){
            toast(`Ï£ºÍ∞Ñ Ï≤¥Ï§ëÏù¥${myInfo.weight-preWeight.weight}kg Ï™òÏäµÎãàÎã§. Í¥ÄÎ¶¨ÌïòÏÑ∏Ïöî.`);
        }else if(showToast &&(preWeight.weight-myInfo.weight)>=5){
            toast(`Ï£ºÍ∞Ñ Ï≤¥Ï§ëÏù¥${preWeight.weight-myInfo.weight}kgÏúºÎ°ú ÎÑàÎ¨¥ Í∏âÍ≤©Ìûà Îπ†Ï°åÏäµÎãàÎã§.`);
        }
    },[showToast])

    useEffect(()=>{
        if(preWeight.weight<myInfo.weight)
        {
            if((myInfo.weight-preWeight.weight)>=3){
                setShowToast(true);
            }
            else
                setShowToast(false); 
        }
        else if(preWeight.weight>myInfo.weight){
            if((preWeight.weight-myInfo.weight)>=5)
                setShowToast(true);
            else
                setShowToast(false);
        }
    },[preWeight,myInfo])


    return (
        <div id={styles.main}>
            <p className={styles.welcome}>{myInfo.name}Îãò ÌôòÏòÅÌï©ÎãàÎã§</p>
            {myInfo.target_weight!==null && myInfo.target_weight<myInfo.weight ?(
                <p className={styles.weight_status}>ÌòÑÏ†ú Ï≤¥Ï§ëÏùÄ{myInfo.weight}kgÏù¥Í≥† Î™©ÌëúÏ≤¥Ï§ë{myInfo.target_weight}kgÍπåÏßÄ {myInfo.weight-myInfo.target_weight}kg ÎπºÏïºÌï©ÎãàÎã§.</p>
            ):myInfo.target_weight!==null && myInfo.target_weight>myInfo.weight ?(
                <p className={styles.weight_status}>ÌòÑÏ†ú Ï≤¥Ï§ëÏùÄ{myInfo.weight}kgÏù¥Í≥† Î™©ÌëúÏ≤¥Ï§ë{myInfo.target_weight}kgÍπåÏßÄ {myInfo.target_weight-myInfo.weight}kg Ï∞åÏõåÏïºÌï©ÎãàÎã§.</p>
            ):myInfo.target_weight!==null && myInfo.target_weight===myInfo.weight?(
                <p>Ï∂ïÌïòÌï©ÎãàÎã§!üéâÎ™©ÌëúÏ≤¥Ï§ëÏù∏{myInfo.target_weight}kgÏóê ÎèÑÎã¨ÌñàÏîÅÎãàÎã§.</p>
            )
            :(
                <p className={styles.weight_status}>ÌòÑÏ†ú Ï≤¥Ï§ëÏùÄ{myInfo.weight}kg ÏûÖÎãàÎã§.</p>
            )
            }

            <div>
                <ToastContainer
                position="top-right" // ÏïåÎûå ÏúÑÏπò ÏßÄÏ†ï
                autoClose={3000} // ÏûêÎèô off ÏãúÍ∞Ñ
                hideProgressBar={false} // ÏßÑÌñâÏãúÍ∞ÑÎ∞î Ïà®ÍπÄ
                closeOnClick // ÌÅ¥Î¶≠ÏúºÎ°ú ÏïåÎûå Îã´Í∏∞
                rtl={false} // ÏïåÎ¶º Ï¢åÏö∞ Î∞òÏ†Ñ
                pauseOnFocusLoss // ÌôîÎ©¥ÏùÑ Î≤óÏñ¥ÎÇòÎ©¥ ÏïåÎûå Ï†ïÏßÄ
                draggable // ÎìúÎûòÍ∑∏ Í∞ÄÎä•
                pauseOnHover // ÎßàÏö∞Ïä§Î•º Ïò¨Î¶¨Î©¥ ÏïåÎûå Ï†ïÏßÄ
                theme="light"
                // limit={1} // ÏïåÎûå Í∞úÏàò Ï†úÌïú
            />
                <p className={styles.today}>{today.replace(/(\d{4})-(\d{2})-(\d{2})/, '$1ÎÖÑ$2Ïõî$3Ïùº')}</p>
                <ul id={styles.menu_list}>
                    <li><Link className={styles.menu} to={"/id/"+userId+"/myinfo"}>ÌöåÏõê ÏÉÅÌÉú</Link></li>
                    <li><Link className={styles.menu} to={"/id/"+userId+"/w_input"}>Ï≤¥Ï§ë Î≥ÄÍ≤Ω</Link></li>
                    <li><Link className={styles.menu} to={"/id/"+userId+"/diet"}>ÏãùÎã® Í∏∞Î°ù</Link></li>
                    <li><Link className={styles.menu} to={"/id/"+userId+"/dietRecord"}>Ïù¥Ï†Ñ ÏãùÎã®</Link></li>
                    <li><Link className={styles.menu} to={"/id/"+userId+"/workout"}>Ïö¥Îèô Í¥ÄÎ¶¨</Link></li>
                </ul>   
            </div>
            <div className={styles.introduce}>
                <p>Í±¥Í∞ï Í¥ÄÎ¶¨Î•º ÏúÑÌï¥ <br></br>ÏãùÎã®Í≥º Ïö¥ÎèôÏùÑ Í∏∞Î°ùÌï† Ïàò ÏûàÎäî<br></br> apiÎ•º Íµ¨ÌòÑÌñàÏäµÎãàÎã§.</p>
            </div>
        </div>
    );
}
