import axios from "axios";
import { useEffect, useRef, useState } from "react";
import styles from "../workout.module.css";
export default function WorkOut()
{
    const userId = localStorage.getItem('userId');
    const exerciseRef = useRef(null);
    const [exercises,setExercises] = useState([]);
    const [selectedExercise,setSelectedExercise] = useState(null);

    return(
        <div>
            <div id={styles.workout_regist}>
                <p>운동 기록하기</p>
                <form>
                    <p>
                        <select onChange={(e)=>setSelectedExercise(e.target.value)}>
                            <option value="cardio" ref={exerciseRef}>유산소 운동</option>
                            <option value="resistance" ref={exerciseRef}>무산소 운동</option>
                        </select>
                        <button onClick={()=>{
                            setExercises([...exercises,{type:selectedExercise}]);
                            setSelectedExercise(null);
                        }}>추가</button>
                    </p>
                    {exercises.map((exercise,index)=>(
                        <div key={index}>
                            {exercise.type==="cardio" && <p>유산소 등록창</p>}
                            {exercise.type==="resistance" && <p>무산소 등록창</p>}
                        </div>
                    ))}
                </form>
            </div>
        </div>
    );
}
