import axios from "axios";
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

export default function DietDetail()
{
    const userId = localStorage.getItem('userId');
    const {date} = useParams();
    const [breakfasts,setBreakfasts] = useState([]);
    const [lunchs,setLunchs] = useState([]);
    const [dinners,setDinners] = useState([]);
    useEffect(()=>{
        axios.post('http://localhost:4000/api/diet_detail',{
            id:userId,
            meal_time:"breakfast",
            date:date
        })
        .then((diet)=>{
            setBreakfasts(diet.data);
        })
    },[])

    useEffect(()=>{
        axios.post('http://localhost:4000/api/diet_detail',{
            id:userId,
            meal_time:"lunch",
            date:date
        })
        .then((diet)=>{
            setDiets(diet.data);
        })
    },[])

    useEffect(()=>{
        axios.post('http://localhost:4000/api/diet_detail',{
            id:userId,
            meal_time:"dinner",
            date:date
        })
        .then((diet)=>{
            setDiets(diet.data);
        })
    },[])


    return(
        <div>
            <p>{date}</p>
            {diets.length>0 ?(
                <ul>
                    {diets.map((diet)=>{
                        return(
                            <li key={diet.diet_num}>
                                식사시간:{diet.meal_time}
                                <br/>
                                식품명:{diet.food},식품종류:{diet.food_type},칼로리:{parseInt(diet.kcl)}kcal,단백질:{parseInt(diet.protein)}g{""},
                            지방:{parseInt(diet.fat)}g,탄수화물:{parseInt(diet.carbo)}g,섭취량:{parseInt(diet.serving_size)}g
                            </li>
                        );
                    })
                    }
                </ul>
            ):(
                <p>기록된 식단이 없습니다.</p>
            )

            }


        </div>
    );
}
