import axios from "axios";
import { useEffect, useRef, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import styles from "../food.module.css";

export default function Food()
{
    const [searchResults,setSearchResults] = useState([]);
    const nav = useNavigate();
    const {meal_time} = useParams();
    const searchRef = useRef(null);
    const servingRef = useRef(null);
    const userId=localStorage.getItem('userId');
    const [today,setToday] = useState('');

    useEffect(()=>{
        const timeId = setInterval(()=>{
            setToday(new Date().toLocaleDateString('ko-KR',{year:'numeric',month:'2-digit',day:'2-digit'}).replace(/\./g,'-').split(' ').join("").slice(0,-1));
        },1000);
        return (()=> clearInterval(timeId));
    },[]);

    function searchFood(e)
    {
        e.preventDefault();
        axios.post('http://localhost:4000/api/food_search',{
            search:'%'+searchRef.current.value+'%'
        }).then((food)=>{
            console.log(food);

            setSearchResults(food.data);
        });
        searchRef.current.value ='';
        setSearchResults([]);
    }

    function inputBreakfast(foodData)
    {
        axios.post('http://localhost:4000/api/food',{
            user_id:userId,
            meal_time:meal_time,
            food:foodData.food_name,
            food_type:foodData.food_type,
            serving_size:foodData.serving_size,
            kcl:foodData.kcal,
            carbo:foodData.carbo,
            protein:foodData.protein,   
            fat:foodData.fat,
            date:today 
        }).then(()=>{
            alert('식사가 등록되었습니다.');
            nav(-1);
        }).catch((err)=>
        {
            console.log(err);
        }
        )
    }
    return(
        <div>
            <p>{today}</p>
            <form onSubmit={searchFood}>
                <label>음식 검색</label>
                <p><input type="search" ref={searchRef}/><button>검색</button></p>
            </form>
            {
                searchResults.length>0 &&(
                    <ul>
                        {searchResults.map((food)=>{
                            if(searchResults.length!==0)
                            {
                                return(
                                    <li className={styles.food_list} key={food.food_name}>
                                        식품명:{food.food_name},식품종류:{food.food_type},칼로리:{food.kcal}kcal,단백질:{food.protein}g,{""},
                                        지방:{food.fat}g,탄수화물:{food.carbo}g,제공량:{food.serving_size}g  <button onClick={()=>inputBreakfast(food)}>등록</button>
                                    </li>
                                )
                            }
                            else{
                                return(<p>검색결과가 없습니다.</p>)
                            }
                        })} 
                    </ul>
                )}
        </div>
    );
}
