import axios from "axios";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import styles from "../diet.module.css";
export default function Diet()
{
    const now = new Date();
    const today =now.toISOString().split('T')[0];
    const userId=localStorage.getItem('userId');
    const [breakfasts,setBreakfasts] = useState([]);
    const [lunchs,setLunchs] = useState([]);
    const [dinners,setDinners] = useState([]);
    const [totalK,setTotalK] = useState(null);
    useEffect(()=>{
        axios.post('http://localhost:4000/api/breakfast_list',{
            meal_time:'breakfast',
            date:today
        }).then((food_list)=>{
            setBreakfasts(food_list.data);
        })
    },[]);

    useEffect(()=>{
        axios.post('http://localhost:4000/api/breakfast_list',{
            meal_time:'lunch',
            date:today
        }).then((food_list)=>{
            setLunchs(food_list.data);
        })
    },[]);

    useEffect(()=>{
        axios.post('http://localhost:4000/api/breakfast_list',{
            meal_time:'dinner',
            date:today
        }).then((food_list)=>{
            setDinners(food_list.data);
        })
    },[]);

    useEffect(()=>{
        totalKcl();
    },[breakfasts,lunchs,dinners]);
    function totalKcl(){
        let totalkcl = 0;
        breakfasts.forEach((food)=>{
            totalkcl +=food.kcl;
        });
        lunchs.forEach((food)=>{
            totalkcl +=food.kcl;
        });
        dinners.forEach((food)=>{
            totalkcl +=food.kcl;
        });
        setTotalK(totalkcl);
    }

    return (
        <div>
            <p>{totalK}</p>
            <p><Link className ={styles.meal_time}to={"/id/"+userId+"/diet/breakfast"}>아침</Link></p>
            <ul>
                {breakfasts.map((food)=>{

                    return(
                    <li key={food.food}>
                    식품명:{food.food},식품종류:{food.food_type},칼로리:{food.kcl}kcal,단백질:{food.protein}g,{""},
                    지방:{food.fat}g,탄수화물:{food.carbo}g,제공량:{food.serving_size}g
                    </li>
                )})

                }
            </ul>
            <p><Link className={styles.meal_time} to={"/id/"+userId+"/diet/lunch"}>점심</Link></p>
            <ul>
                {lunchs.map((food)=>{

                    return(
                    <li key={food.food}>
                    식품명:{food.food},식품종류:{food.food_type},칼로리:{food.kcl}kcal,단백질:{food.protein}g,{""},
                    지방:{food.fat}g,탄수화물:{food.carbo}g,제공량:{food.serving_size}g
                    </li>
                )})
                }
            </ul>   
            <p><Link className={styles.meal_time} to={"/id/"+userId+"/diet/dinner"}>저녁</Link></p>
            <ul>
                {dinners.map((food)=>{

                    return(
                    <li key={food.food}>
                    식품명:{food.food},식품종류:{food.food_type},칼로리:{food.kcl}kcal,단백질:{food.protein}g,{""},
                    지방:{food.fat}g,탄수화물:{food.carbo}g,제공량:{food.serving_size}g
                    </li>
                )})
                }
            </ul>   
        </div>
    );
}
