import axios from "axios";
import { useEffect, useRef, useState } from "react";
import styles from "../workout.module.css";
export default function WorkOut()
{
    const userId = localStorage.getItem('userId');
    const [selectedExercise,setSelectedExercise] = useState("cardio");
    const exerciseRef = useRef(null);
    const hoursRef = useRef(null);
    const minuteRef = useRef(null);
    const [today,setToday] = useState('');

    useEffect(()=>{
        const timeId = setInterval(()=>{
            setToday(new Date().toLocaleDateString('ko-KR',{year:'numeric',month:'2-digit',day:'2-digit'}).replace(/\./g,'-').split(' ').join("").slice(0,-1));
        },1000);
        return (()=> clearInterval(timeId));
    },[]);

    function cardio()
    {
        axios.post('http://localhost:4000/api/cardio',{
            cardio_id:userId,
            cardio_name:exerciseRef.current.value,
            duration:hoursRef.current.value+":"+minuteRef,
            date:today
        })
    }
    return(
        <div>
            <div id={styles.workout_regist}>
                <p>운동 기록하기</p>

                <p>
                    <select className={styles.exc_type} onChange={(e)=>setSelectedExercise(e.target.value)}>
                        <option value="cardio">유산소 운동</option>
                        <option value="resistance">무산소 운동</option>
                    </select>

                </p>
                {selectedExercise === "cardio" ? 
                (
                    <form onSubmit={cardio}>
                        <p><label className={styles.exc_label}>운동이름</label><input type="text" placeholder="유산소운동 이름" ref={exerciseRef}/></p>
                        <p>
                            <label className={styles.exc_label}>운동시간</label>
                            <input type="number" placeholder="시간" min="0" max="6" ref={hoursRef}/>
                            <input type="number" placeholder="분" min="0" max="60" ref={minuteRef}/>

                        </p>
                        <button>기록</button>
                    </form>
                )
                :(
                    <p>무산소 화면</p>
                )

                }

            </div>
        </div>
    );
}
